import React, { useEffect } from 'react'
import { Table } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux';
import { FaTrashAlt } from "react-icons/fa";
import { FaEdit } from "react-icons/fa";
import { deleteList, getList } from '../../store/actions/ListActions';
import { DELETE_LIST } from '../../store/constants/ListTypes';

const ListTable = () => {
    const dispatch = useDispatch();
    const lists = useSelector((state) => state.lists);
    console.log(lists);
    useEffect(() => {
        dispatch(getList())
    }, [])
    return (
        <React.Fragment>
            {!!lists.length &&
            <Table className="list-table" striped hover size="sm">
                <thead>
                <tr>
                    <th>Name</th>
                    <th>Edit</th>
                    <th>Delete</th>
                </tr>
                </thead>
                <tbody>
                {lists.map((list) => (
                    <tr key={list._id}>
                        <td>{list.name}</td>
                        <td><FaEdit /></td>
                        <td><FaTrashAlt onClick={() => dispatch({ type: DELETE_LIST, payload: list._id })} /></td>
                    </tr>
                ))}
                </tbody>
            </Table>
            }
        </React.Fragment>
    )
}

export default ListTable
