import { takeLatest } from "redux-saga/effects";
import { GET_LIST, CREATE_LIST, UPDATE_LIST } from "../constants/ListTypes";
import { handleGetLists, handleNewList, handleUpdateList } from "./handlers/lists";

export default function* watcherSaga() {
    debugger
    yield takeLatest(GET_LIST, handleGetLists);
    yield takeLatest(CREATE_LIST, handleNewList);
    yield takeLatest(UPDATE_LIST, handleUpdateList);
}
