import React, { useEffect, useState } from 'react'
import { Table } from 'react-bootstrap'
import { FaCheck } from "react-icons/fa";
import { FaTimes } from "react-icons/fa";
import { FaTrashAlt } from "react-icons/fa";
import { FaEdit } from "react-icons/fa";
import { useDispatch, useSelector } from 'react-redux';
import { getTodo } from '../../store/actions/TodoActions';

const TodoTable = ({todos}) => {
    const dispatch = useDispatch();
    const [TodoDataUpdateIdState, setTodoDataUpdateIdState] = useState(initialState)    
    const currentUpdateTodoId = useSelector((state) => state.TodoUpdateId);

    useEffect(() => {
        if(currentUpdateTodoId) {
            dispatch(getTodo(currentUpdateTodoId));
        }
    }, [dispatch, currentUpdateTodoId])
    return (
        <React.Fragment>
            {currentUpdateTodoId &&
            <Table className="list-table mx-4" striped hover size="sm">
                <thead>
                <tr>
                    <th>Status</th>
                    <th>Title</th>
                    <th>Date</th>
                    <th>Edit</th>
                    <th>Delete</th>
                </tr>
                </thead>
                <tbody>
                
                {todos && todos.map((todo) => (
                    <tr key={todo._id}>
                    <td>{todo.status ? <FaCheck /> : <FaTimes />}</td>
                    <td>{todo.title}</td>
                    <td>{todo.date}</td>
                    <td><FaEdit /></td>
                    <td><FaTrashAlt /></td>
                    </tr>
                ))}
                </tbody>
            </Table>            
            }
        </React.Fragment>
    )
}

export default TodoTable
