import React, { useEffect, useState } from 'react'
import { Table } from 'react-bootstrap'
import { FaCheck } from "react-icons/fa";
import { FaTimes } from "react-icons/fa";
import { FaTrashAlt } from "react-icons/fa";
import { FaEdit } from "react-icons/fa";
import { useDispatch, useSelector } from 'react-redux';
import { getTodo } from '../../store/actions/TodoActions';
import { connect } from "react-redux";

const TodoTable = (props) => {
    const [todos, setTodos] = useState([])
    const dispatch = useDispatch();
    
    const currentUpdateTodoId = useSelector((state) => state.TodoUpdateId);
    const myTodos = useSelector((state) => state.todos);
    const filterTodos = useSelector((state) => currentUpdateTodoId!==null ? state.todos.find((todo) => todo.list_id == currentUpdateTodoId) : null);

    useEffect(() => {
        console.log(currentUpdateTodoId);
        if(currentUpdateTodoId) {
            dispatch(getTodo(currentUpdateTodoId));
        }
    }, [currentUpdateTodoId])
    
    useEffect(() => {
        setTodos(myTodos);
        console.log(myTodos);
    }, [dispatch])

    return (
        <React.Fragment>
            {currentUpdateTodoId &&
            <Table className="list-table mx-4" striped hover size="sm">
                <thead>
                <tr>
                    <th>Status</th>
                    <th>Title</th>
                    <th>Date</th>
                    <th>Edit</th>
                    <th>Delete</th>
                </tr>
                </thead>
                <tbody>
                
                {todos && todos.map((todo) => (
                    <tr key={todo._id}>
                    <td><FaCheck /></td>
                    <td>Mark</td>
                    <td>Otto</td>
                    <td><FaEdit /></td>
                    <td><FaTrashAlt /></td>
                    </tr>
                ))}
                </tbody>
            </Table>            
            }
        </React.Fragment>
    )
}

const mapStateToProps = state => {
    return {
      todos: state.title
    };
  };
  export default connect(mapStateToProps)(TodoTable);
