import React, { useState, useEffect } from 'react'
import Stack from 'react-bootstrap/Stack'
import { useDispatch, useSelector } from 'react-redux';
import { Form, Button } from 'react-bootstrap'
import { updateListId, createList, updateList } from '../../store/actions/ListActions';

const TodoListForm = () => {
    const dispatch = useDispatch();

    const [list, setList] = useState({
        name: '',
        todos: [],
    });

    const currentUpdateListId = useSelector((state) => state.listUpdateId);
    const updateListData = useSelector((state) => currentUpdateListId!==null ? state.lists.lists.find((list) => list._id == currentUpdateListId) : null);

    //set local state list 
    useEffect(() => {
        if(updateListData) setList(updateListData);
    }, [updateListData]);
    console.log(updateListData);

    const handleSubmit = () => {
        if(list.name){
            if(currentUpdateListId!==null){
                dispatch(updateList(currentUpdateListId, list));
            }else{
                dispatch(createList(list));
            }
            clear();
        }
    }
    
    const clear = () => {
        dispatch(updateListId(null));
        setList({
            name: '',
            todos: [],
        });
    }
        
    return (
        <React.Fragment>
        </React.Fragment>
    )
}

export default TodoListForm
