import { call, put } from 'redux-saga/effects'
import { createList, getList, updateList } from '../../actions/ListActions';
import { requestGetLists, requestNewList, requestUpdateList } from '../requests/lists';

export function* handleGetLists(action) {
    try{
        const response = yield call(requestGetLists);
        const { data } = response;
        yield put(getList(data));
    }catch(err) {
        console.log(err);
    }
}

export function* handleNewList(action) {
    try{
        const response = yield call(requestNewList);
        const { data } = response;
        yield put(createList(data));
    }catch(err) {
        console.log(err);
    }
}

export function* handleUpdateList() {
    try{
        const response = yield call(requestUpdateList);
        const { data } = response;
        yield put(updateList(data));
    }catch(err) {
        console.log(err);
    }
}
