import axios from 'axios'
const base_url = 'http://localhost:5000'

export const requestGetLists = () => {
    return axios.request({
        method: 'get',
        url: base_url+'/lists' 
    })
}

export const requestNewList = (newList) => {
    return axios.request({
        method: 'post',
        url: base_url+'/lists', 
        body:newList 
    })
}


export const createNewTodo = async (title) => {
    try {
      const todo = await axios.post('todos', {
        title
      })
      return todo.data
    } catch(err) {
      return console.error(err)
    }
  }

export const requestUpdateList = (id, updateList) => {
    return axios.request({
        method: 'post',
        url: base_url+'/lists/'+id, 
        body:updateList 
    })
}
