import axios from 'axios'
const base_url = 'http://localhost:5000'

export const requestGetLists = () => {
    return axios.request({
        method: 'get',
        url: base_url+'/lists' 
    })
}

export const requestNewList = async (action) => {
    return axios.request({
        method: 'post',
        url: base_url+'/lists', 
        body:action.newList 
    })
  }

export const requestUpdateList = (action) => {
    return axios.request({
        method: 'post',
        url: base_url+'/lists/'+action.id, 
        body:action.updateList 
    })
}
