import axios from 'axios'
const base_url = 'http://localhost:5000'

export const requestGetLists = () => {
    return axios.request({
        method: 'get',
        url: base_url+'/lists' 
    })
}

export const requestNewList = (newList) => {
    return axios.request({
        method: 'post',
        url: base_url+'/lists',
        headers: {
            "Content-Type": "application/x-www-form-urlencoded",
        },     
        body:newList 
    })
}

export const requestUpdateList = (id, updateList) => {
    return axios.request({
        method: 'post',
        url: base_url+'/lists/'+id, headers: {
            "Content-Type": "application/x-www-form-urlencoded",
        },    
        body:updateList 
    })
}
