import React, { useEffect } from 'react'
import { Table } from 'react-bootstrap'
import { FaCheck } from "react-icons/fa";
import { FaTimes } from "react-icons/fa";
import { FaTrashAlt } from "react-icons/fa";
import { FaEdit } from "react-icons/fa";
import { useDispatch, useSelector } from 'react-redux';

const TodoTable = () => {
    const dispatch = useDispatch();

    
    const currentUpdateTodoId = useSelector((state) => state.TodoUpdateId);
    const todos = useSelector((state) => currentUpdateListId!==null ? state.lists.find((list) => list._id == currentUpdateListId) : null);

    useEffect(() => {
        console.log(currentUpdateTodoId);
    }, [dispatch, currentUpdateTodoId])
    return (
        <React.Fragment>
            <Table className="list-table mx-4" striped hover size="sm">
                <thead>
                <tr>
                    <th>Status</th>
                    <th>Title</th>
                    <th>Date</th>
                    <th>Edit</th>
                    <th>Delete</th>
                </tr>
                </thead>
                <tbody>
                <tr>
                    <td><FaCheck /></td>
                    <td>Mark</td>
                    <td>Otto</td>
                    <td><FaEdit /></td>
                    <td><FaTrashAlt /></td>
                </tr>
                </tbody>
            </Table>            
        </React.Fragment>
    )
}

export default TodoTable
