import React from 'react'
import Stack from 'react-bootstrap/Stack'
import { Form, Button } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux';

const TodoForm = () => {
    const dispatch = useDispatch();
    const currentUpdateTodoId = useSelector((state) => state.updateTodoId);

    return (
        <React.Fragment>
            <div className="ms-4">
                {currentUpdateTodoId &&
                <Form method="post">
                    <Stack direction="horizontal" gap={3}>
                        <Form.Control className="me-auto" placeholder="Enter The Name of List" />
                        <Form.Control className="me-auto" type="date" />
                        <Button variant="primary w-2" className="todo-btn">Add Todo</Button>
                    </Stack>
                </Form>
                }
            </div>
        </React.Fragment>
    )
}

export default TodoForm
