import { GET_LIST, CREATE_LIST, UPDATE_LIST, DELETE_LIST, UPDATE_LIST_ID } from '../constants/ListTypes';

//action creators
//get lists
export const getList = (lists) => ({
    type:GET_LIST,
    payload:lists
})

//create list
export const createList = (newList)  => ({
    type: CREATE_LIST, 
    payload: newList
    
})

//update List
export const updateList = (id, updatedList) => ({
    type: UPDATE_LIST, 
    payload: updatedList
})

//update list edit id
export const updateListId = (id) => {
    return { 
        type: UPDATE_LIST_ID, 
        payload: id 
    }
}
