import { GET_LIST, CREATE_LIST, UPDATE_LIST, DELETE_LIST, UPDATE_LIST_ID } from '../constants/ListTypes';

//action creators
//get lists
export const getPosts = () => async (dispatch) => {
    try{
        const { data } = await api.fetchPosts();
        dispatch({ type: GET_POSTS, payload: data });
    }catch(error){
        console.log(error);
    }
}
