import React, { useState, useEffect } from 'react'
import Stack from 'react-bootstrap/Stack'
import { useDispatch, useSelector } from 'react-redux';
import { Form, Button } from 'react-bootstrap'

const TodoListForm = () => {
    const dispatch = useDispatch();

    const [list, setList] = useState({
        name: '',
        todos: [],
    });

    const updateListId = useSelector((state) => state.listUpdateId);
    const updateList = useSelector((state) => updateListId!==null ? state.lists.lists.find((list) => list._id == updateListId) : null);

    //set local state list 
    useEffect(() => {
        if(updateList) setupdateListData(updateList);
    }, [updateList]);

    const handleSubmit = () => {
        if(list.name){
            setList({
                name: '',
                todos: [],
            });
        }
    }
    return (
        <React.Fragment>
            <Stack direction="horizontal" gap={3}>
                <Form.Control className="me-auto" value={list.name} onChange={(e) => setList({...list, name: e.target.value})} placeholder="Enter The Name of List" />
                <Button variant="primary" onClick={handleSubmit}>Add</Button>
            </Stack>
        </React.Fragment>
    )
}

export default TodoListForm
