import { GET_LIST, CREATE_LIST, UPDATE_LIST, DELETE_LIST } from '../constants/ListTypes';

export default (lists = [
    { _id:1, name:'ali'}, 
    { _id:2, name:'jamal'}], action) => {
    switch (action.type) {
        case GET_LIST:
        return {
                ...lists,
                lists: action.payload
            };
        case CREATE_LIST:
            return [...lists, action.payload];
        case UPDATE_LIST:
            return lists.map((list) => list._id == action.payload._id ? action.payload : list);
        case DELETE_LIST:
            return lists.filter((list) => list._id !== action.payload);
        default:
            return lists;
    }
}
